mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY R.SMT) AS num, OBJ.OBJ_NO AS obj_no, R.SMT AS smt, DEP.DEPT_NAME AS dept_name, STU.STU_GRADE AS stu_grade, STU.STU_NAME AS stu_name, SUB.SUB_NAME AS sub_name, R.GRADE AS grade, R.GRADE_P AS grade_p FROM OBJECTION OBJ RIGHT JOIN SUBJECT SUB ON SUB.SUB_CODE = OBJ.SUB_CODE INNER JOIN STUDENT STU ON STU.STU_NO = OBJ.STU_NO INNER JOIN RESULTS R ON R.SUB_CODE = OBJ.SUB_CODE AND R.STU_NO = STU.STU_NO INNER JOIN DEPARTMENT DEP ON DEP.DEPT_CODE = STU.DEPT_CODE WHERE R.YEAR = 2024 AND R.SMT = 1
AND SUB.DEPT_CODE = #{department}
AND SUB.SUB_CODE = #{subject}
AND STU.STU_GRADE = #{grade}
