mapper.select:
SELECT ROW_NUMBER() OVER (ORDER BY A.SMT) AS num, C.STU_NO, B.SUB_CODE, A.YEAR, A.SMT, D.DEPT_NAME, D.DEPT_CODE, C.STU_GRADE, C.STU_NAME, B.SUB_NAME, P.PROF_NAME, A.GRADE, A.GRADE_P, E.OBJ_NO FROM RESULTS A INNER JOIN SUBJECT B ON A.SUB_CODE = B.SUB_CODE INNER JOIN STUDENT C ON A.STU_NO = C.STU_NO INNER JOIN DEPARTMENT D ON C.DEPT_CODE = D.DEPT_CODE INNER JOIN PROFESSOR P ON P.PROF_NO = B.PROF_NO LEFT OUTER JOIN OBJECTION E ON A.SUB_CODE = E.SUB_CODE AND A.STU_NO = E.STU_NO WHERE A.YEAR = 2024 AND A.SMT = 1 AND E.OBJ_NO IS NOT NULL AND E.OBJ_STATUS = 'N'
AND D.DEPT_NAME LIKE #{department}
AND B.SUB_NAME LIKE #{subject}
AND C.STU_GRADE LIKE #{grade}
LIMIT #{listLimit} OFFSET #{firstRow}

SELECT COUNT(*) AS total_count FROM RESULTS A INNER JOIN SUBJECT B ON A.SUB_CODE = B.SUB_CODE INNER JOIN STUDENT C ON A.STU_NO = C.STU_NO INNER JOIN DEPARTMENT D ON C.DEPT_CODE = D.DEPT_CODE INNER JOIN PROFESSOR P ON P.PROF_NO = B.PROF_NO LEFT OUTER JOIN OBJECTION E ON A.SUB_CODE = E.SUB_CODE AND A.STU_NO = E.STU_NO WHERE A.YEAR = 2024 AND A.SMT = 1 AND E.OBJ_NO IS NOT NULL AND E.OBJ_STATUS = 'N'
AND D.DEPT_CODE = #{department}
AND B.SUB_CODE = #{subject}
AND C.STU_GRADE = #{grade}
